import React, { useRef, useEffect } from 'react'
import Point from './point'
import DirectionGenerator from './DirectionGenerator'

const Canvas = props => {

  const canvasRef = useRef(null)

  let current = 1;
  let prev = 1;

  let gen = new DirectionGenerator(Math.PI / 2)

  const draw = (ctx, current, prev) => {

    ctx.beginPath()
    ctx.moveTo()
    ctx.lineTo(pos.x, pos.y)
    pos.turn(gen.next(), current)
    ctx.stroke()
  }

  useEffect(() => {

    const canvas = canvasRef.current
    const context = canvas.getContext('2d')

    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    let pos = new Point(ctx.canvas.width, canvas.width)


    let frameCount = 0
    let animationFrameId

    //Our draw came here
    const render = () => {
      const temp = current
      current = current + prev
      prev = temp
      draw(context, current, prev)
      animationFrameId = window.requestAnimationFrame(render)
    }
    render()

    return () => {
      window.cancelAnimationFrame(animationFrameId)
    }
  }, [draw])

  return <canvas ref={canvasRef} {...props} />
}


export default Canvas
